import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import Navigation from './components/Navigation';
import LoginPage from './pages/LoginPage';
import SignupPage from './pages/SignupPage';
import UploadPage from './pages/UploadPage';
import BrowsePage from './pages/BrowsePage';
import FileDetailPage from './pages/FileDetailPage';
import AdminPanelPage from './pages/AdminPanelPage';
// Add Three.js imports
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Stars } from '@react-three/drei';

function Background3D() {
  return (
    <Canvas style={{ position: 'fixed', top: 0, left: 0, zIndex: 0, width: '100vw', height: '100vh' }} camera={{ position: [0, 0, 10], fov: 75 }}>
      <ambientLight intensity={0.5} />
      <directionalLight position={[5, 5, 5]} intensity={0.7} />
      <Stars radius={100} depth={50} count={5000} factor={4} saturation={0} fade speed={1} />
      <mesh position={[0, 0, 0]}>
        <torusKnotBufferGeometry args={[2, 0.5, 100, 16]} />
        <meshStandardMaterial color={'#007bff'} metalness={0.7} roughness={0.2} />
      </mesh>
      <OrbitControls enableZoom={false} enablePan={false} autoRotate autoRotateSpeed={1.5} />
    </Canvas>
  );
}

function App() {
  return (
    <AuthProvider>
      <Router>
        <div style={{ position: 'relative', minHeight: '100vh', zIndex: 1 }}>
          <Background3D />
          <div style={{ position: 'relative', zIndex: 2 }}>
            <Navigation />
            <Routes>
              <Route path="/" element={<Navigate to="/browse" />} />
              <Route path="/login" element={<LoginPage />} />
              <Route path="/signup" element={<SignupPage />} />
              <Route path="/upload" element={<UploadPage />} />
              <Route path="/browse" element={<BrowsePage />} />
              <Route path="/files/:id" element={<FileDetailPage />} />
              <Route path="/admin" element={<AdminPanelPage />} />
            </Routes>
          </div>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
