import React, { useState, useEffect } from 'react';
import api from '../utils/api';
import { useParams } from 'react-router-dom';

function FileDetailPage() {
  const { id } = useParams();
  const [file, setFile] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchFile = async () => {
      try {
        const response = await api.get(`/api/files/${id}`);
        setFile(response.data);
      } catch (err) {
        setError('Failed to load file details');
      }
    };
    fetchFile();
  }, [id]);

  if (error) {
    return <p style={{color: 'red'}}>{error}</p>;
  }

  if (!file) {
    return <p>Loading...</p>;
  }

  const renderPreview = () => {
    const fileType = file.fileType;
    if (fileType.startsWith('image/')) {
      return <img src={`/uploads/${file.fileName}`} alt={file.title} style={{maxWidth: '100%'}} />;
    } else if (fileType === 'application/pdf') {
      return <embed src={`/uploads/${file.fileName}`} type="application/pdf" width="100%" height="600px" />;
    } else {
      return <p>No preview available for this file type.</p>;
    }
  };

  return (
    <div>
      <h2>{file.title}</h2>
      <p>Subject: {file.subject}</p>
      <p>Uploader: {file.uploaderName}</p>
      <p>Uploaded: {new Date(file.uploadDate).toLocaleDateString()}</p>
      <p>Description: {file.description || 'No description'}</p>
      <div>
        {renderPreview()}
      </div>
      <a href={`/api/files/${file._id}/download`} target="_blank" rel="noopener noreferrer">Download File</a>
      <p>Downloads: {file.downloadCount}</p>
    </div>
  );
}

export default FileDetailPage;
